INSERT INTO `blog` VALUES (239, 265, 1, 1, '【齐声向暴力说不】<br /><br />去年6至12月，香港各区累计共740组交通灯被破坏，超过52 800米的栏杆及约21 800平方米的行人路路砖被拆走。<br /><br />暴徒大肆破坏铁路和道路设施，令交通出现混乱，危及司机和行人安全。<br /><br />没有暴徒的恶行，社会才能回复安宁。市民大众切勿对暴力视而不见，要齐声向暴力说不。<br /><br /><a  href=\"https://m.weibo.cn/search?containerid=231522type%3D1%26t%3D10%26q%3D%23%E9%A6%99%E6%B8%AF%E8%AD%A6%E5%AF%9F%23&isnewpage=1&luicode=10000011&lfid=100103type%3D60%26q%3D%E9%A6%99%E6%B8%AF\" data-hide=\"\"><span class=\"surl-text\">#香港警察#</span></a> ', '香港 ', -1, 0.0000000000, -1.00, 'https://m.weibo.cn/status/Ip1TW80Le?mblogid=Ip1TW80Le&luicode=10000011&lfid=100103type%3D60%26q%3D%E9%A6%99%E6%B8%AF', '4459655121909552', '2020-01-13 03:32:27', 4);
