INSERT INTO `blog` VALUES (304, 270, 1, 1, '【港警“一哥”遭反对派议员逼迫辞职 不卑不亢反驳后硬气起身离场】#港警一哥遭谴责后硬气离场# 据深圳卫视报道 1月16日，香港警务处处长邓炳强出席中西区区议会第二次大会。对于一些议员的合理质询，邓炳强积极回应。然而会议尾声，有议员提出临时动议谴责邓炳强包庇“警暴”，要求他下台。邓炳强随即起身离场。http://t.cn/A6vXJGaC', '香港 ', -1, 0.0000000000, -1.00, 'https://m.weibo.cn/status/IpRzGwjw8?mblogid=IpRzGwjw8&luicode=10000011&lfid=100103type%3D60%26q%3D%E9%A6%99%E6%B8%AF', '4461641447701524', '2020-01-17 10:21:37', 4);
