INSERT INTO `blog` VALUES (306, 296, 1, 1, '【<a  href=\"https://m.weibo.cn/search?containerid=231522type%3D1%26t%3D10%26q%3D%232019%E5%B9%B4%E8%B5%B4%E6%B8%AF%E6%B8%B8%E5%AE%A2%E5%87%8F%E5%B0%9114.2%25%23&extparam=%232019%E5%B9%B4%E8%B5%B4%E6%B8%AF%E6%B8%B8%E5%AE%A2%E5%87%8F%E5%B0%9114.2%25%23&luicode=10000011&lfid=100103type%3D60%26q%3D%E9%A6%99%E6%B8%AF\" data-hide=\"\"><span class=\"surl-text\">#2019年赴港游客减少14.2%#</span></a>！上半年增长，自7月起下跌】昨天，香港旅游发展局公布消息称，2019年初步访港旅客数字逾5590万人次。2019年上半年，访港旅客增长13.9%，但受社会事件影响，旅客数字自7月起下跌，随后的月份跌势加剧，全年旅客数字按年下跌14.2%。（总台央视记者金东） ', '香港 ', 1, 1.0000000000, -1.00, 'https://m.weibo.cn/status/IpKO6jdph?mblogid=IpKO6jdph&luicode=10000011&lfid=100103type%3D60%26q%3D%E9%A6%99%E6%B8%AF', '4461381304579771', '2020-01-17 10:21:37', 4);
