INSERT INTO `blog` VALUES (404, 398, 2, 1, '【#伊朗愿和欧洲协商伊核协议# 最高领袖：欧洲认美国当主子 还想让伊朗也下跪】1月17日，伊朗最高领袖表示愿意与欧洲协商《伊核协议》条款，但绝不和美国协商。即使英、法、德满嘴谎言，伊朗也不惧怕与他们谈判。当日是他8年来首次主持布道会，会上他表示英、法、德是美国的奴仆，正等着让伊朗也下跪。http://t.cn/A6vKBZUP', '正常', -1, 0.0000000000, 1.00, 'https://m.weibo.cn/status/Iq27lypxz?mblogid=Iq27lypxz&luicode=10000011&lfid=100103type%3D60%26q%3D%E4%BC%8A%E6%9C%97', '4462046718201333', '2020-01-19 03:10:26', 2);
