INSERT INTO `blog` VALUES (451, 427, 2, 1, '【美媒发布#伊朗导弹击中客机全过程#视频：两枚导弹半分钟内先后发射】《纽约时报》公布1月8日乌克兰客机坠毁前最新视频，画面显示两枚伊朗导弹先后在半分钟内发射，分别击中客机，客机被击中后在空中发出巨大火光并不断下坠。伊朗军方11日发布声明称，客机被伊朗军方“非故意”击落，事故系“人为错误”所致。#乌克兰客机遇袭全过程#@时差视频 http://t.cn/A6vqy54i', '正常', 1, 0.0000000000, 1.00, 'https://m.weibo.cn/status/Ipzr1cXL4?mblogid=Ipzr1cXL4&luicode=10000011&lfid=100103type%3D60%26q%3D%E4%BC%8A%E6%9C%97', '4460944153089650', '2020-01-19 03:13:18', 1);
