INSERT INTO `blog` VALUES (503, 456, 2, 0, '伊朗逮捕拍下导弹击落乌克兰客机视频的人，理由是关系国家安全。<br /><br />伊朗媒体报道称，伊朗革命卫队已将一名上周发布导弹击中飞机视频的人拘留。<br /><br />但一名最初发布这段视频的伦敦伊朗裔记者坚称，他的消息来源者是安全的，伊朗当局逮捕了错误的人。 ', '国家安全 ', 1, 0.0000000000, -1.00, 'https://m.weibo.cn/status/IpARfn0q6?mblogid=IpARfn0q6&luicode=10000011&lfid=100103type%3D60%26q%3D%E4%BC%8A%E6%9C%97', '4460998855483162', '2020-01-19 03:13:18', 3);
