INSERT INTO `blog` VALUES (507, 449, 2, 1, '【<a  href=\"https://m.weibo.cn/search?containerid=231522type%3D1%26t%3D10%26q%3D%23%E4%B8%AD%E5%9B%BD%E5%9B%BD%E5%A5%A5%E4%B8%8D%E6%95%8C%E4%BC%8A%E6%9C%97%23&extparam=%23%E4%B8%AD%E5%9B%BD%E5%9B%BD%E5%A5%A5%E4%B8%8D%E6%95%8C%E4%BC%8A%E6%9C%97%23&luicode=10000011&lfid=100103type%3D60%26q%3D%E4%BC%8A%E6%9C%97\" data-hide=\"\"><span class=\"surl-text\">#中国国奥不敌伊朗#</span></a>，三战三败0进球】U23亚洲杯暨奥预赛C组最后一轮比赛打响，中国国奥0-1不敌伊朗国奥。门将陈威屡献神扑难救主，国奥最后时刻被争议点球绝杀，中国国奥三战皆负零进球，小组出局，无缘东京奥运会。 <a data-url=\"http://t.cn/A6vcmNn7\" href=\"https://m.weibo.cn/p/index?containerid=2304444461091124871178&url_type=39&object_type=video&pos=1&luicode=10000011&lfid=100103type%3D60%26q%3D%E4%BC%8A%E6%9C%97\" data-hide=\"\"><span class=\'url-icon\'><img style=\'width: 1rem;height: 1rem\' src=\'https://h5.sinaimg.cn/upload/2015/09/25/3/timeline_card_small_video_default.png\'></span><span class=\"surl-text\">央视体育的微博视频</span></a> ', '正常', -1, 0.0000000000, 1.00, 'https://m.weibo.cn/status/IpE9gujUu?mblogid=IpE9gujUu&luicode=10000011&lfid=100103type%3D60%26q%3D%E4%BC%8A%E6%9C%97', '4461125347226378', '2020-01-19 03:13:18', 2);
INSERT INTO `blog` VALUES (508, 272, 2, 1, '#乌客机被伊朗2枚导弹击中视频#【美媒再曝新视频：伊朗2枚导弹击中乌客机，间隔不到30秒】乌克兰一架客机8日在伊朗境内坠毁，几天后伊朗军方承认误击乌客机。据多家美媒14日报道，当天一段新监控录像显示，这架客机从德黑兰起飞后，先后被2枚伊朗导弹击中，间隔不到30秒。\n\n美国福克斯新闻称，《纽约时报》证实了这段视频的真实性。据报道，2枚导弹从距客机8英里(约合13公里)的伊朗军事基地发射。被第一枚导弹击中后，客机应答器停止工作。不到30秒后，客机又被第二枚导弹击中。\n\n福克斯新闻提到，视频显示，客机着火了，但并未立即坠入地面。其试图返回机场，但最终爆炸并坠毁。\n\n福克斯新闻援引《纽约时报》报道称，14日凌晨2时许，一名伊朗用户将这段视频上传至社交媒体。#乌克兰客机被伊朗导弹击中视频# 详戳：http://t.cn/A6vGmp1Z  @环视频 http://t.cn/A6vGBsPi', '正常', -1, 0.0000000000, 1.00, 'https://m.weibo.cn/status/IpyLB52df?mblogid=IpyLB52df&luicode=10000011&lfid=100103type%3D60%26q%3D%E4%BC%8A%E6%9C%97', '4460918471200149', '2020-01-19 03:13:18', 2);
INSERT INTO `blog` VALUES (509, 460, 2, 1, '【美媒曝光新监控视频：伊朗2枚导弹击中乌客机 间隔不到30秒】14日，《纽约时报》公布乌克兰坠毁客机新监控视频，显示这架客机从德黑兰起飞后，先后被两枚伊朗导弹击中，间隔不到30秒。客机着火后，并未立即坠入地面，而是试图返回机场，但最终爆炸并坠毁。14日凌晨2时许，一名伊朗用户将这段视频上传至社交媒体。（环球网）#乌客机被伊朗2枚导弹击中视频#http://t.cn/A6vqAUoa', '正常', -1, 0.0000000000, 1.00, 'https://m.weibo.cn/status/Ipz5muG99?mblogid=Ipz5muG99&luicode=10000011&lfid=100103type%3D60%26q%3D%E4%BC%8A%E6%9C%97', '4460930727311855', '2020-01-19 03:13:18', 2);
